package com.kosenin.weatherapp2.Helpers;		import java.util.List;		import com.google.gson.annotations.SerializedName;public class ListItem{	@SerializedName("dt")	private int dt;	@SerializedName("temp")	private Temp temp;	@SerializedName("snow")	private double snow;	@SerializedName("deg")	private int deg;	@SerializedName("weather")	private List<WeatherItem> weather;	@SerializedName("humidity")	private int humidity;	@SerializedName("pressure")	private double pressure;	@SerializedName("clouds")	private int clouds;	@SerializedName("speed")	private double speed;	public void setDt(int dt){		this.dt = dt;	}	public int getDt(){		return dt;	}	public void setTemp(Temp temp){		this.temp = temp;	}	public Temp getTemp(){		return temp;	}	public void setSnow(double snow){		this.snow = snow;	}	public double getSnow(){		return snow;	}	public void setDeg(int deg){		this.deg = deg;	}	public int getDeg(){		return deg;	}	public void setWeather(List<WeatherItem> weather){		this.weather = weather;	}	public List<WeatherItem> getWeather(){		return weather;	}	public void setHumidity(int humidity){		this.humidity = humidity;	}	public int getHumidity(){		return humidity;	}	public void setPressure(double pressure){		this.pressure = pressure;	}	public double getPressure(){		return pressure;	}	public void setClouds(int clouds){		this.clouds = clouds;	}	public int getClouds(){		return clouds;	}	public void setSpeed(double speed){		this.speed = speed;	}	public double getSpeed(){		return speed;	}}